import React, { useState } from 'react'
import './App.css';
import PersonForm from './components/PersonForm'
import Content from './components/Content'
import Filter from './components/Filter'
import Notification from './components/Notification'
import personService from './services/persons'

const App = () => {
  const [ allPersons, setPersons ] = useState([
  { name: 'Arto Hellas', number: '040-123456' },
  { name: 'Ada Lovelace', number: '39-44-5323523' },
  { name: 'Dan Abramov', number: '12-43-234345' },
  { name: 'Mary Poppendieck', number: '39-23-6423122' }
]) 
  // const [allPersons,setPersons]=useState([])
  const [newName,setNewName] =  useState('')
  const [newNumber,setNewNumber] =  useState('')
  const [newFilter,setNewFilter]=useState('')
  const [message, setMessage] = useState(null);

  React.useEffect(() => {
    personService.getAll()
    .then(initialPersons => {
      setAllPersons(initialPersons)
    })
  },[]);

  const addPerson =(event) =>{
    event.preventDefault()
    const person = allPersons.filter((person) =>
    person.name === newName
    )
    const personToAdd =person[o]
    const updatedPerson = { ...personToAdd,number: newNumber}
    if(person.length !== 0) {
      if(window.confirm('${personToAdd.name} is already added to the phonebook, replace the old number with new one ?')) {
        personService
        .upadte(updatedPerson.id,updatedPerson).then(returnedPerson => {
          console.log('${returnedPerson.name} successfully updated')
          setAllPersons(allPersons.map(personItem => personItem.id !== personToAdd.id ? personItem : returnedPerson))
          setNewName('')
          setNewNumber('')
          setMessage(
            '${updatedPerson.name} was successfully updated'
          )
          setTimeout (() => {
            setMessage(null)
          },5000)
        })
        .catch((error) => {
          console.log(error);
          setAllPersons(allPersons.filter(person => person.id !== updatedPerson.id))
          setNewName('')
          setNewNumber('')
          setMessage(
            `[ERROR] ${updatedPerson.name} was already deleted from server`
          )
          setTimeout(()=> {
            setMessage(null)
          },5000)
        })
      }
    }
    else {
      const personToAdd = {
        name : newName,
        number : newNumber
      }
      personService
      .create(personToAdd)
      .then(returnedPerson => {
        setAllPersons(allPersons.concat(returnedPerson))
        setNewName('')
        setNewNumber('')
        setMessage(
          '${newName} was successfully added'
        )
        setTimeout(()=> {
          setMessage(null)
        },5000)
      })
      .catch(error => {
        setMessage(
          '[ERROR] ${error.response.data.error}'
        )
        setTimeout(()=> {
          setMessage(null)
        },5000)
        console.log(error.response.data)
      })
    }
  }

  const deletePerson = (id) => {
    const filteredPerson = allPersons.filter(person => person.id === id)
    const personName = filteredPerson[0].name
    const personId = filteredPerson[0].id
    if (window.confirm(`Delete ${personName} ?`)) {
      personService
        .remove(personId)
      console.log(`${personName} successfully deleted`)
      setMessage(
        `${personName} was successfully deleted`
      )
      setAllPersons(allPersons.filter(person => person.id !== personId))
      setTimeout(() => {
        setMessage(null)
      }, 5000)
    }
  }

  const handleNameChange = (event) =>
  {
    //console.log(event.target.value)
    setNewName(event.target.value)
  }
  const handleFilterChange = (event) => {
    setNewFilter(event.target.value)
    const regex = new RegExp( newFilter, 'i' );
    const filteredPersons = () => allPersons.filter(person => person.name.match(regex))
    setPersons(filteredPersons)
  }
  const handleNumberChange = (event) =>
  {
    //console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <Notification message ={message} />
      <Filter value ={newFilter} onChange={handleFilterChange} />
      <h2>Add new person</h2>
      <PersonForm onSubmit={addPerson} newName={newName} handleNameChange={handleNameChange}  newNumber={newNumber} handleNumberChange={handleNumberChange}  />
      <h2>Numbers</h2>
      <Content persons={persons} allPersons={allPersons} deletePerson={deletePerson} />
